// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract VehicleHistory {

    struct Record {
        uint256 timestamp;
        string dataType;      // Bao duong, Thay linh kien, Tai nan
        uint256 odometer;     // So km
        string description;   // Mo ta chi tiet
        address recorder;     // Ai ghi du lieu
    }

    struct Vehicle {
        string vin;
        Record[] records;
        bool exists;
    }

    mapping(string => Vehicle) private vehicles;

    event VehicleCreated(string vin);
    event RecordAdded(string vin, string dataType, uint256 odometer);

    modifier onlyAuthorized() {
        // Demo: cho phep moi nguoi ghi
        // Thuc te: chi garage/doi tac duoc cap quyen
        _;
    }

    function createVehicle(string memory _vin) public {
        require(!vehicles[_vin].exists, "Vehicle already exists");

        vehicles[_vin].vin = _vin;
        vehicles[_vin].exists = true;

        emit VehicleCreated(_vin);
    }

    function addRecord(
        string memory _vin,
        string memory _dataType,
        uint256 _odometer,
        string memory _description
    ) public onlyAuthorized {
        require(vehicles[_vin].exists, "Vehicle does not exist");

        vehicles[_vin].records.push(
            Record({
                timestamp: block.timestamp,
                dataType: _dataType,
                odometer: _odometer,
                description: _description,
                recorder: msg.sender
            })
        );

        emit RecordAdded(_vin, _dataType, _odometer);
    }

    function getRecordCount(string memory _vin) public view returns (uint256) {
        require(vehicles[_vin].exists, "Vehicle does not exist");
        return vehicles[_vin].records.length;
    }

    function getRecord(string memory _vin, uint256 _index)
        public
        view
        returns (
            uint256 timestamp,
            string memory dataType,
            uint256 odometer,
            string memory description,
            address recorder
        )
    {
        require(vehicles[_vin].exists, "Vehicle does not exist");
        require(_index < vehicles[_vin].records.length, "Index out of bounds");

        Record memory r = vehicles[_vin].records[_index];
        return (r.timestamp, r.dataType, r.odometer, r.description, r.recorder);
    }
}
